'use client';

import { ensureNativeModulesAreInstalled } from 'expo-modules-core/src/ensureNativeModulesAreInstalled';
import type { EventsMap } from 'expo-modules-core/src/ts-declarations/EventEmitter';
import type { ExpoGlobal } from 'expo-modules-core/src/ts-declarations/global';

ensureNativeModulesAreInstalled();

export type NativeModule<TEventsMap extends EventsMap = Record<never, never>> =
  typeof ExpoGlobal.NativeModule<EventsMap>;
export const NativeModule: typeof ExpoGlobal.NativeModule = globalThis.expo.NativeModule;
